import React, { useState, useCallback } from "react";
import BotAvatar from "./components/BotAvatar";
import ChatBubble from "./components/ChatBubble";
import Controls from "./components/Controls";
import ThemeToggle from "./components/ThemeToggle";
import VoiceOutput from "./components/VoiceOutput";
import { useVoiceInput } from "./hooks/useVoiceInput";
import { useVoiceOutput } from "./hooks/useVoiceOutput";
import { useDialogueLoop } from "./hooks/useDialogueLoop";
import "./styles.css";

export default function App() {
  const [theme, setTheme] = useState("light");
  const [chat, setChat] = useState([]); // {sender: 'user'|'bot', text}
  const [isActive, setIsActive] = useState(false);
  const [isMuted, setIsMuted] = useState(false);

  const { speak, audioRef } = useVoiceOutput({
    onEnd: () => setListening(true)
  });
  const { listening, setListening, handleUserSpeech } = useDialogueLoop({
    speak: isMuted ? async () => {} : speak,
    onBotMessage: msg => setChat(c => [...c, { sender: msg.role === "assistant" ? "bot" : "user", text: msg.content }])
  });
  useVoiceInput({
    onResult: txt => {
      if (txt.trim()) handleUserSpeech(txt.trim());
    },
    listening: isActive && listening,
    autoRestart: true
  });

  const handleStart = useCallback(() => {
    setChat([]);
    setIsActive(true);
    setListening(true);
  }, [setChat, setIsActive, setListening]);

  const handleMute = useCallback(() => setIsMuted(m => !m), []);
  const handleEnd = useCallback(() => {
    setIsActive(false);
    setListening(false);
  }, [setIsActive, setListening]);

  const handleThemeToggle = useCallback(() => setTheme(t => (t === "dark" ? "light" : "dark")), []);

  return (
    <div className={`app-container ${theme}`}>
      <ThemeToggle theme={theme} onToggle={handleThemeToggle} />
      <div className="main-area">
        <div className="avatar-area">
          <BotAvatar />
        </div>
        <div className="chat-area">
          {chat.map((msg, i) => (
            <ChatBubble key={i} text={msg.text} sender={msg.sender} />
          ))}
        </div>
        <Controls
          onStart={handleStart}
          onMute={handleMute}
          onEnd={handleEnd}
          isActive={isActive}
          isMuted={isMuted}
        />
        <VoiceOutput ref={audioRef} />
      </div>
    </div>
  );
}
